  public void setMonster(Monster m){
    this.monsterModel = m;
    this.jL_name.setText(m.getName());
    this.jL_showlevel.setText(Integer.toString(m.getBasicValue()));
    this.jL_showlevelagainstcultist.setText((Integer.toString(m.getSpecialValue())));
    this.prizeView.setPrize(m.getPrize());
    BadConsequence bad = this.monsterModel.getBadConsequence();
    this.repaint();
    this.revalidate();//necesario porque el tama√±o del badconsequence es variable
    this.jP_badconsequence.removeAll();
    if(bad instanceof BadDeath){
      BadDeathView bcView = new BadDeathView();
      bcView.setBC((BadDeath) bad);      
      this.jP_badconsequence.add(bcView);
    }else if(bad instanceof BadNumberOfTreasures){
      BadNumberView bcView = new BadNumberView();
      bcView.setBC((BadNumberOfTreasures) bad);
      this.jP_badconsequence.add(bcView);
    }else if(bad instanceof BadTreasureKind){
      BadSpecificView bcView = new BadSpecificView();
      bcView.setBC((BadTreasureKind) bad);
      this.jP_badconsequence.add(bcView);
    }    
    this.jP_badconsequence.repaint();
    this.jP_badconsequence.revalidate();
    this.repaint();
    this.revalidate();
  }

